import { bootstrapApplication, importProvidersFrom } from '@angular/core';
import { provideRouter } from '@angular/router';
import { provideHttpClient, withInterceptorsFromDi } from '@angular/common/http';
import { AppComponent } from './app.component';
import { routes } from './app/app.routes';
import { AuthModule, AuthHttpInterceptor } from '@auth0/auth0-angular';
import { AUTH0_DOMAIN, AUTH0_CLIENT_ID, AUTH0_AUDIENCE, API_BASE } from './app/auth.config';
import { HTTP_INTERCEPTORS } from '@angular/common/http';
bootstrapApplication(AppComponent, {
  providers: [provideRouter(routes), provideHttpClient(withInterceptorsFromDi()), importProvidersFrom(AuthModule.forRoot({
    domain: AUTH0_DOMAIN,
    clientId: AUTH0_CLIENT_ID,
    authorizationParams: {
      redirect_uri: window.location.origin + '/callback',
      audience: AUTH0_AUDIENCE
    },
    httpInterceptor: {
      allowedList: [{
        uri: API_BASE + '/*',
        tokenOptions: {
          authorizationParams: {
            audience: AUTH0_AUDIENCE
          }
        }
      }]
    }
  })), {
    provide: HTTP_INTERCEPTORS,
    useClass: AuthHttpInterceptor,
    multi: true
  }]
});
